from collections import Counter


class Solution:
    def checkInclusion(self, s1: str, s2: str) -> bool:
        len_1, len_2 = len(s1), len(s2)
        if len_1 > len_2: return False

        map_s1 = Counter(s1)

        for i in range(len_1):  # 初始化窗口
            if s2[i] in map_s1:
                map_s1[s2[i]] -= 1
            if max(list(map_s1.values())) == 0:
                return True

        for p in range(len_1, len_2):  # 滑窗
            if s2[p] in map_s1:
                map_s1[s2[p]] -= 1
            if s2[p - len_1] in map_s1:
                map_s1[s2[p - len_1]] += 1
            if max(list(map_s1.values())) == 0:
                return True

        return False

if __name__ == '__main__':
    # 
    # 
    #
    so = Solution()
    s1 = input("s1:")
    s2 = input("s2:")
    res = so.checkInclusion(s1, s2)
    print(res)